Python<pre><code>class ResponsiveLayoutWidget(BaseWidget):
    """响应式布局组件"""

    def __init__(self):
        super().__init__()
        self._columns = []
        self._mobile_stack = True
        self._gap = "20px"

    def add_column(self, widget, width_desktop="1fr", width_mobile="100%"):
        """添加列"""
        self._columns.append({
            'widget': widget,
            'width_desktop': width_desktop,
            'width_mobile': width_mobile
        })
        return self

    def set_mobile_stack(self, stack: bool):
        """设置移动端是否堆叠"""
        self._mobile_stack = stack
        return self

    def render(self) -&gt; str:
        desktop_grid = " ".join(col['width_desktop'] for col in self._columns)

        # 生成响应式CSS
        responsive_css = f"""
        &lt;style&gt;
            .responsive-grid {{
                display: grid;
                grid-template-columns: {desktop_grid};
                gap: {self._gap};
                margin: 10px 0;
            }}

            @media (max-width: 600px) {{
                .responsive-grid {{
                    grid-template-columns: {"1fr" if self._mobile_stack else desktop_grid};
                }}
            }}

            .responsive-column {{
                min-width: 0; /* 防止内容溢出 */
            }}
        &lt;/style&gt;
        """

        # 生成列内容
        columns_html = ""
        for col in self._columns:
            columns_html += f"""
            &lt;div class="responsive-column"&gt;
                {col['widget'].render()}
            &lt;/div&gt;
            """

        return f"""
        {responsive_css}
        &lt;div class="responsive-grid"&gt;
            {columns_html}
        &lt;/div&gt;
        """

